/* eslint-disable @typescript-eslint/explicit-function-return-type */
import * as React from 'react';

import { useComponentUtils } from '../../../../utils/useComponentsUtils';
import { IContainerProps } from './IContainerProps';

export const useContainerStyles = (props: IContainerProps) => {
  const {maxWith } = props;
  const { getBaseStyles } = useComponentUtils();
  const containerStyles = React.useCallback(
    (containerWidth: number, containerHeight: number): React.CSSProperties => {
      if (!containerWidth || !containerHeight) return {};
      const baseStyles = getBaseStyles(props, containerWidth, containerHeight);
      return {
        ...baseStyles,
        maxWidth: maxWith,
      };
    }, [getBaseStyles, maxWith, props]);
    
  return { containerStyles };
};
