/* eslint-disable @typescript-eslint/explicit-function-return-type */
import * as React from "react";

import { ILayoutConfig, ILayoutProps } from "./ILayoutProps";

import { css } from "@emotion/css";
import { useComponentUtils } from "../../utils";

export const useLayoutStyles = (props: ILayoutProps & { layoutConfig?: ILayoutConfig }) => {
  const { maxWith,  } = props;
  const { getBaseStyles } = useComponentUtils();

  const rootStyles = React.useCallback(
    (LayoutWidth: number, LayoutHeight: number): React.CSSProperties => ({
      ...getBaseStyles(props, LayoutWidth, LayoutHeight),
      maxWidth: maxWith,
    }),
    [getBaseStyles, maxWith, props]
  );

  const layoutGridStyles = css({
    display: "grid",
    overflow: "hidden",
  });

  return {
    rootStyles,
    layoutGridStyles,
  };
};