/* eslint-disable @typescript-eslint/explicit-function-return-type */
/* eslint-disable @typescript-eslint/no-unused-vars */
import * as React from 'react';

 
 
import { ISpaceProps } from './ISpaceProps';
import { useComponentUtils } from '../../utils';
import { THorizontalSpacing, TVerticalSpacing } from '../../types';

export const useSpaceStyles = (props: ISpaceProps) => {
  const { verticalSpacing, horizontalSpacing } = props;
  const { getSpacing } = useComponentUtils();
  const spaceStyles = React.useCallback(
    (containerWidth: number): React.CSSProperties => {
      if (!containerWidth ) return {};
      

       
      if (horizontalSpacing) {
        const width = getSpacing<THorizontalSpacing>(horizontalSpacing as string, containerWidth, "horizontal");
        return {
         width: width,
        };
      }
      if (verticalSpacing) {
        const height = getSpacing<TVerticalSpacing>(verticalSpacing as string, containerWidth, "vertical");
        return {
          height: height,
        };
      }
      return {};
    },
    [getSpacing, horizontalSpacing, verticalSpacing]
  );

  return { spaceStyles };
};
