/* eslint-disable @typescript-eslint/explicit-function-return-type */
import * as React from 'react';

import { css } from '@emotion/css';

 
import { IDataGridProps } from './IDataGridProps';
import { useComponentUtils } from '../../utils';

export const useDataGridStyles = <T,>(props: IDataGridProps<T>) => {
  const { styles } = props;
  const { getBaseStyles } = useComponentUtils();

  const dataGridStyles = React.useCallback(
    (containerWidth: number, containerHeight: number) => {
      const baseStyles = getBaseStyles(props, containerWidth, containerHeight);
      return {
        datagridStyles: css({
          ...baseStyles,
          ...styles,
         
        }),
        rowSelection: css({
          ":hover": {
            cursor: "pointer",
          }
        }),
      };
    },
    [getBaseStyles, props, styles]
  );



  return { dataGridStyles };
};
